import React, { useEffect, useState } from "react";

const Footer = () => {
  const [utcTime, setutcTime] = useState("");
  const d = new Date();
  let times, hh, mm;
  const getUtcTime = () => {
    hh = d.getUTCHours();
    mm = d.getUTCMinutes();
    hh = hh < 10 ? "0" + hh : hh;
    mm = mm < 10 ? "0" + mm : mm;
    times = hh + ":" + mm;
    return times;
  };

  const initialUtc = getUtcTime();

  const [time, setTime] = useState(initialUtc);

  useEffect(() => {
    const interval = setInterval(() => {
      setTime(getUtcTime);
    }, 30000);

    return () => clearInterval(interval);
  }, []);

  return <div className="footer">{time}UTC</div>;
};

export default Footer;
