import React, { useContext, useEffect, useState } from "react";
import { MapContainer, TileLayer, useMap, Marker, Popup } from "react-leaflet";
import { Icon } from "leaflet";
import L from "leaflet";
import FlightInfo from "./FlightInfo";
import { FlightData } from "./FlightDataProvider";
const MapView = () => {
  const [data, setData] = useState(null);
  const filter = useContext(FlightData);
  console.log(filter);
  const icon = L.icon({
    iconUrl: "flight.png",
    iconSize: [25, 25],
    className: "flight-icon",
  });
  async function fetchData() {
    const response = await fetch("http://localhost:3500/flightInfo/");
    const json = await response.json();
    setData(json);
  }

  useEffect(() => {
    fetchData();
  }, [filter.filterFlight]);

  const filterData = () => {
    let filterFlight = data.filter(
      (flight) => flight.flightNo == filter.filterFlight
    );

    setData(filterFlight);
  };
  useEffect(() => {}, []);
  console.log("data", data);
  return (
    <MapContainer
      className="map-container"
      center={[51.505, -0.09]}
      zoom={3}
      scrollWheelZoom={false}
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      {data
        ? data.map((flight) => (
            <Marker
              key={flight.flightId}
              position={flight.position}
              icon={icon}
            >
              <Popup className="popUp-body">
                <FlightInfo flight={flight} />
              </Popup>
            </Marker>
          ))
        : console.log("loading")}
    </MapContainer>
  );
};

export default MapView;
