import React, { useEffect, useState } from 'react'
import { MapContainer, TileLayer, useMap,Marker,Popup } from 'react-leaflet'
import { Icon } from 'leaflet'
import FlightInfo from './FlightInfo'
const MapView = () => {
  const [data, setData] = useState(null);
    const position = [51.505, -0.09]
    const flightIcon=new Icon({
        iconUrl:'flight.png',
        iconSize:[25,25]
    });

    async function fetchData() {
      const response = await fetch('http://localhost:3500/flightInfo/');
      const json = await response.json();
      setData(json);
    }

    useEffect(() => {
      
      fetchData();
    }, []);

    console.log("data",data)
  return (
    
<MapContainer className='map-container' center={[51.505, -0.09]} zoom={3} scrollWheelZoom={false}>
        <TileLayer
      attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    />


     <Marker position={[51.505, -0.09]} icon={flightIcon}>
      <Popup>
      
        <FlightInfo />
      </Popup>
    </Marker>

    <Marker position={[55.505, -0.09]} icon={flightIcon}>
      <Popup>
      
        <FlightInfo />
      </Popup>
    </Marker>
    </MapContainer>
    

    
  )
}

export default MapView
