import React, { useEffect, useState } from "react";

const Footer = () => {
  const [utcTime, setutcTime] = useState("");
  const d = new Date();
  let times, hh, mm;
  const getUtcTime = () => {
    hh = d.getUTCHours();
    mm = d.getUTCMinutes();
    hh = hh < 10 ? "0" + hh : hh;
    mm = mm < 10 ? "0" + mm : mm;
    times = hh + ":" + mm;
    // setutcTime(times);

    return times;
  };

  const initialUtc = getUtcTime();

  const [time, setTime] = useState("");

  useEffect(() => {
    const interval = setInterval(() => {
      setTime(getUtcTime);
    }, 30000); // Update the time every 60 seconds (60000 milliseconds)

    return () => clearInterval(interval); // Clean up the interval when the component unmounts
  }, []);
  //   setInterval(() => getUtcTime, 1000);

  return <div className="footer">{time}UTC</div>;
};

export default Footer;
