import React, { useState } from "react";

const Footer = () => {
  const [utcTime, setutcTime] = useState("");
  const d = new Date();

  const getUtcTime = () => {
    let hh = d.getUTCHours();
    let mm = d.getUTCMinutes();
    hh = hh < 10 ? "0" + hh : hh;
    mm = mm < 10 ? "0" + mm : mm;
    let day = hh + ":" + mm;
  };

  setInterval(setutcTime);

  return <div>{day}</div>;
};

export default Footer;
