import React, { useState } from "react";

const Footer = () => {
  const [utcTime, setutcTime] = useState("");
  const d = new Date();

  let hh = d.getUTCHours();
  let mm = d.getUTCMinutes();
  hh = hh < 10 ? "0" + hh : hh;
  mm = mm < 10 ? "0" + mm : mm;
  let day = hh + ":" + mm;
  const [utc, setutc] = useState("");

  return <div>{day}</div>;
};

export default Footer;
