import React, { useContext, useEffect, useState } from "react";
import { FlightData, FlightDataProvider } from "./FlightDataProvider";

const Footer = () => {
  const initialUtc = getUtcTime();
  const { setFilterData, setTime, time, filterFlight } = useContext(FlightData);

  useEffect(() => {
    const interval = setInterval(() => {
      setTime(getUtcTime);
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  return <div className="footer">{time}UTC</div>;
};

export default Footer;
