import React, { useState } from "react";

const Footer = () => {
  const [utcTime, setutcTime] = useState("");
  const d = new Date();
  let time, hh, mm;
  const getUtcTime = () => {
    hh = d.getUTCHours();
    mm = d.getUTCMinutes();
    hh = hh < 10 ? "0" + hh : hh;
    mm = mm < 10 ? "0" + mm : mm;
    time = hh + ":" + mm;
    setutcTime(time);
  };

  setInterval(getUtcTime, 60000);

  return <div>{time}</div>;
};

export default Footer;
